"""Read the results generated by pipeline6
"""
import sys
import argparse

import torch


def parse_args():
    """Parse input arguments"""
    parser = argparse.ArgumentParser()

    parser.add_argument(
        '-f', '--fname', type=str,
        help='Path to the results file',
    )

    args = parser.parse_args()
    return args


def main(args):
    """Main function"""
    data = torch.load(args.fname)
    all_skip = data['all_skip']

    total_frames = len(all_skip)
    skip_frames = sum(all_skip)
    unskip_frames = total_frames - skip_frames
    print('Skipped frames:', skip_frames)
    print('Unskipped frames:', unskip_frames)
    return 0


if __name__ == '__main__':
    sys.exit(main(parse_args()))
